convert UTC timestamp to Tehran time","convert_to_tehran() {","    local utc_timestamp=\"$1\"","    local tehran_timestamp=$(TZ='Asia/Tehran' date -d \"${utc_timestamp}\" +\"%Y-%m-%d %H:%M:%S\")","    echo \"$tehran_timestamp\"","}","","# Output parent directory","OUTPUT_PARENT_DIR=\"query_results\"","","# Create the output parent directory if not exists","mkdir -p \"$OUTPUT_PARENT_DIR\"","","# Get the total number of queries to be executed","total_queries=$((${#HOST_NAMES[@]} * ${#TIME_RANGES[@]} * ${#IP_PORTS[@]}))","current_query=0","","# Loop through each combination of time range, host, IP, PORT, and execute the curl command","for host_name in \"${HOST_NAMES[@]}\"; do","    # Create a new CSV file for each host in a directory named 'host_name_csv'","    output_dir=\"${OUTPUT_PARENT_DIR}/${host_name}-csv\"","    mkdir -p \"$output_dir\"","    output_csv=\"${output_dir}/${host_name}.csv\"","","    # Initialize the CSV file with the header","    header=\"Start_Time,End_Time\"","","    for metric_file in \"${METRIC_FILES_ARRAY[@]}\"; do","        while IFS= read -r metric_name; do","            # Extract the prefix from the metric filename","            metric_prefix=$(basename \"$metric_file\" _metric_list.txt)","            header=\"${header},${metric_prefix}_${metric_name#netdata.}\"","        done < \"$metric_file\"","    done","    echo \"$header\" > \"$output_csv\"","","    for line in \"${TIME_RANGES[@]}\"; do","        # Split the start and end times from the line","        IFS=',' read -r start_time_utc end_time_utc <<< \"$line\"","","        # Convert the timestamps to Tehran time","        start_time_tehran=$(convert_to_tehran \"$start_time_utc\")","        end_time_tehran=$(convert_to_tehran \"$end_time_utc\")","","        for ip_port in \"${IP_PORTS[@]}\"; do","            # Split IP and PORT from the IP:PORT pair","            ip_address=\"${ip_port%:*}\"","            port=\"${ip_port#*:}\"","","            line_values=\"$start_time_tehran,$end_time_tehran\"","","            for metric_file in \"${METRIC_FILES_ARRAY[@]}\"; do","                if [[ -f \"$metric_file\" ]]; then","                    while IFS= read -r metric_name; do","                        # Extract the prefix from the metric filename","                        metric_prefix=$(basename \"$metric_file\" _metric_list.txt)","                        ","                        # Construct the curl command with the current metric_name, start time, end time, host, IP address, and port","                        curl_command=\"curl -sG 'http://${ip_address}:${port}/query' --data-urlencode \\\"db=${DATABASE}\\\" --data-urlencode \\\"q=SELECT ${metric_prefix}(\\\\\\\"value\\\\\\\") FROM \\\\\\\"${metric_name}\\\\\\\" WHERE (\\\\\\\"host\\\\\\\" =~ /^${host_name}$/) AND time >= '${start_time_utc}' AND time <= '${end_time_utc}'\\\"\"","","                        # Execute the curl command and get the values","                        query_result=$(eval \"${curl_command}\")","                        values=$(echo \"$query_result\" | jq -r '.results[0].series[0].values[] | .[1]')","","                        # Append the values to the line_values string","                        line_values+=\",$values\"","                    done < \"$metric_file\"","                else","                    echo \"Metric file not found: $metric_file\"","                fi","            done","","            # Append the line_values to the CSV file","            echo \"$line_values\" >> \"$output_csv\"","","            # Loop through each metric file for the second query","            for metric_file in \"${METRIC_FILES_ARRAY[@]}\"; do","                if [[ -f \"$metric_file\" ]]; then","                   while IFS= read -r metric_name; do","                        # Extract the prefix from the metric filename","                        metric_prefix=$(basename \"$metric_file\" _metric_list.txt)","","                        # Construct the curl command for query 2 with the current metric_name, start time, end time, host, IP address, and port","                        query2_curl_command=\"curl -sG 'http://${ip_address}:${port}/query' --data-urlencode \\\"db=${DATABASE}\\\" --data-urlencode \\\"q=SELECT ${metric_prefix}(\\\\\\\"value\\\\\\\") FROM /\"${metric_name}/\" WHERE (\\\\\\\"host\\\\\\\" =~ /^${host_name}$/) AND time >= '${start_time_utc}' AND time <= '${end_time_utc}' GROUP BY time(10s) fill(none)\\\"\"","                       ","                        # Get the query2 output and store it in a variable","                        query2_output=$(eval \"$query2_curl_command\")","                      ","                        python3 image-render16.py \"$query2_output\" \"$host_name\"","","                   done < \"$metric_file\"","                else","                    echo \"Metric file not found: $metric_file\"","                fi","            done","        done","    done","done","","# Print completion message after the progress bar","echo -ne \"${BOLD}Progress: [###########################################################] 100% \\n ${RESET}\"","echo -e \"${BOLD}CSV and Image are saved in the '$OUTPUT_PARENT_DIR' directory for each host${RESET}\""],"stylingDirectives":[[{"start":0,"end":11,"cssClass":"pl-c"},{"start":0,"end":2,"cssClass":"pl-c"}],[],[],[],[],[{"start":0,"end":34,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":16,"end":37,"cssClass":"pl-s"},{"start":16,"end":17,"cssClass":"pl-pds"},{"start":36,"end":37,"cssClass":"pl-pds"}],[{"start":15,"end":31,"cssClass":"pl-s"},{"start":15,"end":16,"cssClass":"pl-pds"},{"start":30,"end":31,"cssClass":"pl-pds"}],[{"start":13,"end":31,"cssClass":"pl-s"},{"start":13,"end":14,"cssClass":"pl-pds"},{"start":30,"end":31,"cssClass":"pl-pds"}],[{"start":9,"end":19,"cssClass":"pl-s"},{"start":9,"end":10,"cssClass":"pl-pds"},{"start":18,"end":19,"cssClass":"pl-pds"}],[],[{"start":0,"end":15,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{